<template>
  <div class="video">
    <scroll ref="scroll" class="video-content" :data="videos"
            :pullup="true" :pulldown="true" :listenScroll="true"
            v-on:scrollToEnd="_getMoreData" v-on:pulldown="reload" v-on:scroll="scroll">
      <div>
        <tip :changeTip="changeTip"></tip>
        <h2>热门视频</h2>
        <ul>
          <router-link v-for="(item,index) in videos" :to="{ path: 'video/detail', query: {id: item.id}}" tag="li" :key="index">
            <div class="left">
              <img v-lazy="item.thumb">
              <img src="data:image/png;base64," alt="" class="img-wrap">
            </div>
            <div class="right">
              <p>{{item.title}}</p>
              <span v-text="item.comments_total+'评论'"></span>
            </div>
          </router-link>
        </ul>
      </div>
      <loading :alpha="true" v-show="loading"></loading>
    </scroll>
  </div>
</template>

<script>
  import Tip from 'components/tip/tip'
  import Scroll from 'components/scroll/scroll'
  import Loading from 'components/loading/loading'
  import { getVodeoList } from 'api/video'
  export default {
  	data() {
  		return {
  		  loading: false,
        changeTip: false,
        videos: [],
        url: '',
  		}
  	},
    mounted() {
      this.$nextTick(()=>{
        this._getVideos()
        this.$refs.scroll.refresh()
      })
    },
    methods: {
  	  reload() {
        this.url = ''
        this._getVideos()
      },
      _getVideos() {
        if (!this.url) {
          this.loading = true
        }
        getVodeoList(this.url).then((res)=>{
          this.loading = false
          this.changeTip = false
          if("OK" === res.statusText) {
            const { articles, next } = res.data
            this.videos = [...this.videos, ...articles]
            this.url = next
          }
        })
      },
      _getMoreData(){
        this._getVideos(++this.page)
        this.$refs.scroll.refresh()
      },
      scroll(pos) {
        this.changeTip = pos.y > 50
      }
    },
    components: {
      Scroll,
      Loading,
      Tip
    }
  }
</script>

